import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-card-produto',
  templateUrl: './card-produto.component.html',
  styleUrls: ['./card-produto.component.css'],
})
export class CardProdutoComponent implements OnInit {
  @Input() foto: string = '';
  @Input() nomeProduto: string = '';
  @Input() promocao: boolean = true;
  @Input() idProduto: number = 0;
  @Input() dataValidade: string = '';
  @Input() precoProduto: number = 0;

  constructor() {}

  ngOnInit(): void {}
}
<div class="card shadow mt-5 mr-5" style="width: 18rem">
  <img [src]="foto" class="card-img-top" alt="..." />
  <div class="card-body">
    <h5 class="card-title">
      {{ nomeProduto | uppercase }}
      <span *ngIf="promocao; else elseBlock" class="badge badge-success"
        >Promoção</span
      >
      <ng-template #elseBlock>
        <span class="badge badge-primary">Aproveite</span>
      </ng-template>
    </h5>
    <p class="card-text">Identificação {{ idProduto | number: "2.0-0" }}
      <br>
      Validade: {{dataValidade | date: 'shortDate'}}

    </p>
    <a href="#" class="btn btn-primary float-right">
      {{ precoProduto | currency: "BRL"}}</a
    >
  </div>
</div>
