import { IComentario } from './../../../model/IComentario.model';
import { ComentarioService } from './../../../services/comentario.Service';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-cadastrar-comentario',
  templateUrl: './cadastrar-comentario.component.html',
  styleUrls: ['./cadastrar-comentario.component.css'],
})
export class CadastrarComentarioComponent implements OnInit {
  comentario: IComentario = {
    nome: '',
    descricao: '',
    data: new Date()
  };

  constructor(private comentarioService: ComentarioService, private router: Router) {}

  ngOnInit(): void {}

  salvarProduto(): void {
    this.comentarioService.cadastrar(this.comentario).subscribe(retorno => {
      this.comentario = retorno;
      this.comentarioService.exibirMensagem(
        'Sistema',
        `${this.comentario.nome} foi cadastrado com sucesso. ID ${this.comentario.id}`,
        'toast-success'
      );
      this.router.navigate(['/comentario']);
    });
  }
}
<h1 class="my-3 text-primary">Cadastrar Comentario</h1>
<div class="card shadow">
  <form class="m-3">
    <!-- Inicio row nome -->
    <div class="row">
      <div class="col md-12">
        <div class="form-group">
          <label for="nome">Nome:</label>
          <input
            type="text"
            class="form-control"
            placeholder="Digite o nome do Produto"
            name="nomeProduto"
            [(ngModel)]="comentario.nome"
          />
        </div>
      </div>
    </div>
    <!-- Fim row nome -->
    <div class="row">
      <div class="col md-6">
        <div class="form-group">
          <label for="nome">Comentario: </label>
          <input
            type="date"
            class="form-control"
            name="validade"
            [(ngModel)]="comn.descricao"
          />
        </div>
      </div>
    </div>
    <div class="row">
      <div class="col">
        <button class="btn btn-default" routerLink="/produtos">Voltar</button>
        <button class="btn btn-success float-right" (click)="salvarProduto()">Salvar</button>
      </div>
    </div>
  </form>
</div>
