import { Component } from '@angular/core';

interface Comment {
  author: string;
  content: string;
}

@Component({
  selector: 'app-comentarios-produtos',
  templateUrl: './comentarios-produtos.component.html',
  styleUrls: ['./comentarios-produtos.component.css']
})
export class ComentariosProdutosComponent {
  comments: Comment[] = [
    {
      author: 'João',
      content: 'Ótimo post! Obrigado por compartilhar.'
    },
    {
      author: 'Maria',
      content: 'Gostei muito desse conteúdo. Parabéns!'
    },
    {
      author: 'Pedro',
      content: 'Excelente explicação. Continue assim!'
    }
  ];
}
<div class="card">
  <div class="card-body">
    <h5 class="card-title">{{ comments.author }}</h5>
    <p class="card-text">{{ comments.content }}</p>
  </div>
</div>
<div class="container">
  <h1>Comentários</h1>

</div>
